import apiPath from '../../Filepaths';


export default {



    data(){
        return {
            
            gebruikers: [],    
            message: "Ruben",
            
        } 
        
    },

    mounted() {
        this.fetchGebruikers();


    },

    methods: {
      
        async fetchGebruikers() {
            let response = await fetch(`${apiPath}gebruiker`);
            this.gebruikers = await response.json();
        },

        async verwijder(id) {
            await fetch(`${apiPath}gebruiker?gebruiker=${id}`, {
                method: "POST",                             

            })
        }
    }

}
