import apiPath from '../../Filepaths';
import VueMonthlyPicker from 'vue-monthly-picker';





export default {
    data() {
        return {
            winkel: {
                Winkelnaam: null,
            },
            winkels: [],
            producten: [],
            toggle: 0,
            search:'',
            headers: [
                {
                    text: 'Product',
                    align: 'left',
                    sortable: true,
                    value: 'naam'
                },

                {
                    text: 'Prijs',
                    value: 'prijs'
                }
              ],
        }
    },

    mounted() {
        this.fetchWinkels();
    },

    watch: {
        toggle: function (val) {
            this.winkel = this.winkels[val];
            this.fetchProducten();
        },
    },

    components: {
        VueMonthlyPicker
    },

    methods: {

        check(datum) {
            console.log("hallo");
            console.log(datum);
        },

        prijsGeformatteerd(prijs) {
            prijs = prijs.toString();
            while (prijs.length < 3) {
                prijs = "0" + prijs;
            }
            return prijs.substring(0, prijs.length - 2) + "." + prijs.substring(prijs.length - 2);
        },

        async fetchWinkels() {
            let response = await fetch(`${apiPath}winkel`);
            this.winkels = await response.json();
            this.winkel = this.winkels[0];
            this.fetchProducten();
        },

        async fetchProducten() {
            let response = await fetch(`${apiPath}product/winkel/${this.winkel.winkelnaam}`);
            this.producten = await response.json();
        }
    }
}
